export function state(
  p: Promise<any>,
)Promise<'pending' | 'fulfilled' | 'rejected'> {
  const t = {};
  return Promise.race([p, t]).then(
    (v: any) => (v === t ? 'pending' : 'fulfilled'),
    () => 'rejected',
  ) as Promise<'pending' | 'fulfilled' | 'rejected'>;
}

